conn=sqlite3.connect("PPS.db")
 
conn.execute('''Create Table IF NOT EXISTS Client
             (ClientID         TEXT NOT NULL,
              Title             TEXT (4)            NOT NULL,
              Forname           TEXT (15)           NOT NULL,
              Surname           TEXT (15)           NOT NULL,
              DateofBirth     TEXT (10)           NOT NULL,
              Address           TEXT (20)           NOT NULL,
              Postcode          TEXT (8)            NOT NULL,
              City         TEXT (15)           NOT NULL,
              ContactNumber     TEXT (11)           NOT NULL,
              ContactEmail      TEXT (25)           NOT NULL,
              PRIMARY KEY (ClientID)
              );''')
 
conn.execute('''Create Table IF NOT EXISTS Invoice
             (InvoiceID         TEXT      NOT NULL,
              ClientID            TEXT         NOT NULL,
              Address           TEXT (20)           NOT NULL,
              IssueDate          TEXT (10)            NOT NULL,
              DueDate         TEXT (10)           NOT NULL,
              SalesCode       TEXT (6)           NOT NULL,
              Quantity     INTEGER           NOT NULL,
              Description     TEXT(20)     NOT NULL,
              Rate      INTEGER           NOT NULL,
              TotalAmountDue   INTEGER   NOT NULL,
              PRIMARY KEY (InvoiceID)
              FOREIGN KEY (ClientID) REFERENCES Client(ClientID)
              );''')
 
conn.execute('''Create Table IF NOT EXISTS Supplier
             (SupplierID        TEXT     NOT NULL,
              SupplierName      TEXT(20)    NOT NULL,
              Address           TEXT(30)    NOT NULL,
              Postcode           TEXT(8)    NOT NULL,
              City              TEXT(20)    NOT NULL,
              ContactTitle      TEXT(4)    NOT NULL,
              ContactName    TEXT(30)    NOT NULL,
              ContactNumber     INT(11)     NOT NULL,
              ContactEmail      TEXT(25)    NOT NULL,
              PRIMARY KEY(SupplierID)
              );''')
 
conn.execute('''Create Table IF NOT EXISTS Purchases
            (PurchaseID  TEXT      NOT NULL,
            SupplierID      TEXT     NOT NULL,
            ClientID        TEXT(6)     NOT NULL,
            IssueDate       TEXT(10)    NOT NULL,
            DueDate         TEXT(10)    NOT NULL,
            PurchaseCode    TEXT(6)     NOT NULL,
            Description     TEXT(20)    NOT NULL,
            Total           INTEGER     NOT NULL,
            PRIMARY KEY(PurchaseID)
            FOREIGN KEY(SupplierID) REFERENCES Supplier(SupplierID)
            FOREIGN KEY(ClientID) REFERENCES Client(ClientID)
            );''')
 
conn.execute('''Create Table IF NOT EXISTS Transactions
             (TransactionID TEXT     NOT NULL,
              InvoiceID     TEXT      NOT NULL,
              Made          INTEGER     NOT NULL,
              PurchaseID    TEXT      NOT NULL,
              Spent         INTEGER     NOT NULL,
              Profit        INTEGER     NOT NULL,
              PRIMARY KEY(TransactionID)
              FOREIGN KEY(InvoiceID) REFERENCES Invoice(InvoiceID)
              FOREIGN KEY(PurchaseID) REFERENCES Purchases(PurchaseID)
              );''')
 
conn.execute('''Create Table IF NOT EXISTS Stock
            (StockID             TEXT     NOT NULL,
            StockName           TEXT(20)    NOT NULL,
            StockAmount         INTEGER     NOT NULL,
            StockPricePerUnit   TEXT     NOT NULL,
            PRIMARY KEY(StockID)
            );''')
 
conn.execute('''Create Table IF NOT EXISTS PartsUsed
            (StockID                TEXT     NOT NULL,
            StockNameUsed            TEXT    NOT NULL,
            StockAmountUsed        INTEGER      NOT NULL,
            PricePerUnit       TEXT     NOT NULL,
            TransactionID          TEXT,      
            PRIMARY KEY(StockID, TransactionID)
            FOREIGN KEY(StockID) REFERENCES Stock(StockID)
            FOREIGN KEY(TransactionID) REFERENCES Transactions(TransactionsID)
            );''')
conn.commit()
conn.close()
 
