def DelInv():
 
    chechvalidated = InvoicevalDel()
    
    if chechvalidated:
        conn = sqlite3.connect('PPS.db')
 
        InvoiceID = InvID_Var.get()
 
        sql = "DELETE FROM Invoice WHERE InvoiceID=?"
 
        cursor = conn.cursor()
 
        cursor.execute(sql, (InvoiceID,))
 
        conn.commit()
        conn.close()
        InvEdBox.insert(0, "Record with ID: " + InvoiceID + ", Has been Deleted" )
    return
def DelInvConfirm():
 
    Delinvconfirm = Toplevel()
    Delinvconfirm.geometry("400x200")
    Delinvconfirm.config(bg="#aedaf0")
 
    Prompt = Label(Delinvconfirm,text = "WARNING, PRESSING CONFIRM WILL DELETE THIS RECORD")
    Prompt.config(bg="white")
