def UpdateInv():
 

    chechvalidated = InvoicevalUpdate()
    
    if chechvalidated:
        conn=sqlite3.connect('PPS.db')
        cursor=conn.cursor()
 
        InvoiceID = InvID_Var.get()
        IssueDate = InvDate_Var.get()
        DueDate = InvDueDate_Var.get()
        SalesCode = InvSalesCode_Var.get()
        Description = InvDescription_Var.get()
 
        cursor.execute('''UPDATE Invoice SET Description = ? WHERE InvoiceID = ?''',(Description,InvoiceID))
        cursor.execute('''UPDATE Invoice SET SalesCode = ? WHERE InvoiceID = ?''',(SalesCode,InvoiceID))
        cursor.execute('''UPDATE Invoice SET DueDate = ? WHERE InvoiceID = ?''',(DueDate,InvoiceID))
        cursor.execute('''UPDATE Invoice SET IssueDate = ? WHERE InvoiceID = ?''',(IssueDate,InvoiceID))
 
        conn.commit()
        conn.close
 
        InvEdBox.insert(0, "Record with ID: " + InvoiceID + ", Has been Updated" )

    return
