def CalcTotal(transaction_id):
    chechvalidated = TransactionIDSearchVal()
    
    if chechvalidated: 
        user_code = transaction_id
    
        conn = sqlite3.connect("PPS.db")
        cursor = conn.cursor()
        cursor.execute('''SELECT count(*) FROM PartsUsed WHERE TransactionID = ?''',(user_code,))
        length = cursor.fetchone()
        cursor.execute('''SELECT StockID FROM PartsUsed WHERE TransactionID = ?''',(user_code,))
        array = cursor.fetchall()
        conn.close()
    
    
        amount = 0
        ppu = 0
        count = 0
        Total = 0
        Tot = 0
        Total = float(Total)
        tot = float(Tot)
        for count in range(len(array)):
        
 
        
            user_id = str(array[count])
            user_id = user_id.replace("(",'')
            user_id = user_id.replace("'",'')
            user_id = user_id.replace(")",'')
            user_id = user_id.replace(",",'')


            conn = sqlite3.connect("PPS.db")
            cursor = conn.cursor()
            cursor.execute('''SELECT StockAmountUsed FROM PartsUsed WHERE StockID = ?''', (user_id,))
            for records in cursor:
                amount = records[0]
            amount = float(amount)
                
            conn.close()
  
            conn = sqlite3.connect("PPS.db")
            cursor = conn.cursor()   
            amount = amount + 0
            cursor.execute('''SELECT PricePerUnit FROM PartsUsed WHERE StockID = ?''', (user_id,))
